#define MOTOR_A_1A 2#define MOTOR_A_1B 4#define MOTOR_B_1A 6#define MOTOR_B_1B 8void setup() {  // Motor pins need to be OUTPUT.  pinMode(MOTOR_A_1A, OUTPUT);  pinMode(MOTOR_A_1B, OUTPUT);  pinMode(MOTOR_B_1A, OUTPUT);  pinMode(MOTOR_B_1B, OUTPUT);}void wheelAForwards() {  digitalWrite(MOTOR_A_1A, HIGH);  digitalWrite(MOTOR_A_1B, LOW);}void wheelABackwards() {  digitalWrite(MOTOR_A_1A, LOW);  digitalWrite(MOTOR_A_1B, HIGH);}void wheelAStop() {  digitalWrite(MOTOR_A_1A, LOW);  digitalWrite(MOTOR_A_1B, LOW);}void wheelBBackwards() {  digitalWrite(MOTOR_B_1A, HIGH);  digitalWrite(MOTOR_B_1B, LOW);}void wheelBForwards() {  digitalWrite(MOTOR_B_1A, LOW);  digitalWrite(MOTOR_B_1B, HIGH);}void wheelBStop() {  digitalWrite(MOTOR_B_1A, LOW);  digitalWrite(MOTOR_B_1B, LOW);}void loop() { // use the wheel... methods to drive one motor. wheelAForwards(); delay(1000); wheelAStop(); delay(500); wheelBForwards(); delay(1000); wheelBStop(); delay(1000); wheelAForwards(); wheelBForwards(); delay(2000); wheelAStop(); wheelBStop(); delay(1000); wheelABackwards(); wheelBBackwards(); delay(2000); wheelAStop(); wheelBStop(); delay(1000); }